create table category(category_id integer primary key,category_name varchar(50));
create table product(product_id varchar(30) primary key, product_name varchar(50), product_description varchar(1000), product_price double, currency varchar(10),category_id integer, FOREIGN KEY(category_id) references category(category_id));
create table customer(customer_id varchar(30) primary key , firstname varchar(50), lastname varchar(50), addr1 varchar(100), addr2 varchar(100), addr3 varchar(100), city varchar(50), country varchar(50), zipcode numeric , mobile numeric , email varchar(100));
create table delivery_address(id integer primary key , firstname varchar(50), lastname varchar(50), addr1 varchar(100), addr2 varchar(100), addr3 varchar(100), city varchar(50), country varchar(50), zipcode numeric , mobile numeric , email varchar(100));
create table orders(order_id varchar(30) primary key, customer_id varchar(30), isregistered bool , delivery_addr_id integer , payment_type varchar(30), order_date date , status varchar(30), total double , foreign key(customer_id) references customer(customer_id), foreign key(delivery_addr_id) references delivery_address(id));
create table order_items(id integer primary key , order_id varchar(30), product_id varchar(30), quantity integer, foreign key(order_id) references orders(order_id), foreign key(product_id) references product(product_id));
create table shopping_cart(cart_id integer primary key , customer_id varchar(30) not null, product_id varchar(30) not null, quantity integer not null, foreign key(customer_id) references customer(customer_id), foreign key(product_id) references product(product_id));